import React, { useState, useEffect } from 'react';

const PetsGallaryLanding = () => {
  const [email, setEmail] = useState('');
  const [animateIn, setAnimateIn] = useState(false);

  useEffect(() => {
    setAnimateIn(true);
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Email submitted:", email);
    setEmail('');
  };

  return (
    <div className="bg-gradient-to-br from-gray-200 to-gray-300 min-h-screen flex items-center justify-center p-4">
      <div
        className={`bg-white rounded-3xl overflow-hidden w-full max-w-6xl shadow-2xl transition-all duration-700 ${
          animateIn ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'
        }`}
      >
        <div className="flex flex-col md:flex-row">
          {/* Left Content Section */}
          <div className="p-6 sm:p-8 md:p-12 lg:p-16 md:w-1/2 flex flex-col justify-between">
            <div
              className="mb-6 sm:mb-8 flex items-center transition-transform duration-700"
              style={{ transitionDelay: '200ms', transform: animateIn ? 'translateY(0)' : 'translateY(20px)' }}
            >
              <img src="/logo.png" alt="Pet's Gallery Logo" className="w-10 h-10 sm:w-12 sm:h-12 mr-3 sm:mr-4" />
              <h1 className="text-2xl sm:text-3xl font-bold text-amber-700 tracking-wide font-['Montserrat']">
                Pet's Gallery
              </h1>
            </div>
            <div className="transform transition-transform duration-700" style={{ transitionDelay: '400ms', transform: animateIn ? 'translateY(0)' : 'translateY(20px)' }}>
              <h2 className="text-4xl sm:text-5xl md:text-6xl font-bold text-orange-500 mb-6 sm:mb-8 leading-tight">
                Coming soon...
              </h2>
              <p className="mb-4 sm:mb-6 text-gray-700 text-base sm:text-lg">
                Welcome to Pet's Gallary – your go-to destination for premium pet products and expert care!
              </p>
              <p className="mb-6 sm:mb-8 text-gray-600 text-sm sm:text-base">
                We’re launching soon with a wide range of pet essentials, from food to accessories and grooming services.
                Stay tuned for updates and exclusive offers!
              </p>
              <form onSubmit={handleSubmit} className="mb-8 sm:mb-12 flex flex-col sm:flex-row group">
                <input
                  type="email"
                  placeholder="Email Address"
                  className="px-4 sm:px-6 py-3 sm:py-4 border border-gray-200 rounded-lg sm:rounded-l-lg flex-grow text-gray-800 shadow-sm focus:outline-none focus:ring-2 focus:ring-orange-300 focus:border-transparent transition-all mb-3 sm:mb-0"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
                <button
                  type="submit"
                  className="bg-gradient-to-r from-orange-500 to-orange-600 text-white px-6 sm:px-8 py-3 sm:py-4 rounded-lg sm:rounded-r-lg hover:from-orange-600 hover:to-orange-700 transition-all shadow-md"
                >
                  Subscribe
                </button>
              </form>
            </div>
            <div className="flex flex-col sm:flex-row sm:space-x-8 transform transition-all duration-700" style={{ transitionDelay: '600ms', transform: animateIn ? 'translateY(0)' : 'translateY(20px)' }}>
              <div className="mb-4 sm:mb-0">
                <h3 className="text-amber-800 font-semibold mb-2 text-sm uppercase">OPENING HOURS:</h3>
                <p className="text-gray-700 text-sm">Sunday - Saturday</p>
                <p className="text-gray-700 text-sm">11 am - 8 pm</p>
              </div>
              <div>
                <h3 className="text-amber-800 font-semibold mb-2 text-sm uppercase">LOCATION:</h3>
                <p className="text-gray-700 text-sm">Db, Dubai</p>
              </div>
            </div>
          </div>
          {/* Right Image Section */}
          <div className="bg-gradient-to-br from-amber-50 to-orange-50 md:w-1/2 flex items-center justify-center p-6 relative">
            <img src="/IMG_2168.PNG" alt="Pet's Gallary Website Preview" className="rounded-xl shadow-xl max-w-full w-auto h-auto" />
          </div>
        </div>
      </div>
    </div>
  );
};

export default PetsGallaryLanding;
