import React from "react";

const BestSeller = () => {
  const products = Array(5).fill({
    image: "https://via.placeholder.com/150", // Replace with actual image
    title: "Lorem ipsum dolor sit amet consectetur.",
    price: "₹550",
    discount: "30%",
  });

  return (
    <div className="bg-[#F6F7EB] py-10 px-6">
      <h2 className="text-2xl font-bold mb-6">Our Best Seller</h2>
      <div className="overflow-x-auto whitespace-nowrap">
        <div className="flex gap-6">
          {products.map((product, index) => (
            <div
              key={index}
              className="w-64 bg-white rounded-lg shadow-md p-4 flex-shrink-0"
            >
              {/* Wishlist Icon */}
              <div className="flex justify-between">
                <span className="text-gray-500 cursor-pointer">♡</span>
                <span className="bg-orange-500 text-white text-xs px-2 py-1 rounded-full">
                  {product.discount}
                </span>
              </div>

              {/* Product Image */}
              <img
                src={product.image}
                alt="Product"
                className="w-full h-32 object-contain mt-2"
              />

              {/* Rating */}
              <div className="flex items-center mt-2">
                {[...Array(4)].map((_, i) => (
                  <span key={i} className="text-yellow-500">★</span>
                ))}
                <span className="text-gray-400">★</span>
              </div>

              {/* Product Title */}
              <p className="text-sm text-gray-700 mt-2">{product.title}</p>

              {/* Price */}
              <p className="font-bold text-lg mt-1">{product.price}</p>

              {/* Add to Cart Button */}
              <button className="w-full bg-orange-500 text-white py-2 rounded-md mt-3">
                Add To Cart
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default BestSeller;
