import React from 'react';
import { ArrowUpRight } from 'lucide-react';

const CategorySection = () => {
  return (
    <div className="my-16">
      <div className="max-w-6xl mx-auto px-5 md:px-10">
        <h1 className="text-3xl font-bold mb-6">Our Category</h1>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          {/* Main category box */}
          <div className="h-[200px] bg-white rounded-lg p-6 flex justify-between items-center shadow-sm w-full">
            <span className="text-lg font-medium">Go to All Category</span>
            <ArrowUpRight className="w-6 h-6 text-orange-400" />
          </div>

          {/* Dog category */}
          <div className="h-[200px] bg-[#FFE2CE] rounded-lg p-4 flex justify-between items-center w-full">
            <h2 className="text-2xl font-medium">Dogs</h2>
            <img src="Mask group.png" alt="Dog" className="object-contain h-40 w-40" />
          </div>

          {/* Fish category */}
          <div className="h-[200px] bg-[#FFE2CE] rounded-lg p-4 flex justify-between items-center w-full">
            <h2 className="text-2xl font-medium">Fish</h2>
            <img src="envato-labs-image-edit (6) 1.png" alt="Fish" className="object-contain h-40 w-40" />
          </div>

          {/* Cat & Bird category in the same column (split 50% each) */}
          <div className="md:col-span-2 grid grid-cols-2 gap-4w-[1350px]">
            {/* Cat category */}
            <div className="bg-[#FFE2CE] rounded-lg p-6 flex justify-between items-center w-full">
              <h2 className="text-2xl font-medium">Cats</h2>
              <img src="gold-bengal-cat-white-space 2.png" alt="Cat" className="object-contain h-40 w-40" />
            </div>

            {/* Bird category */}
            <div className="bg-[#FFE2CE] rounded-lg p-6 flex justify-between items-center w-full">
              <h2 className="text-2xl font-medium">Birds</h2>
              <img src="envato-labs-image-edit (7) 1.png" alt="Bird" className="object-contain h-40 w-40" />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CategorySection;
