import React from 'react';
import { Link } from "react-router-dom";
import { Heart, ShoppingCart, User } from 'lucide-react';

const Navbar = () => {
  return (
    <header className="bg-white h-20 px-4 sm:px-10 md:px-20 flex items-center justify-between shadow-md">

      {/* Left side: Logo (Hidden on small screens) */}
      <div className="hidden md:flex items-center">
        <img src="logo png 1.png" alt="Logo" className="h-10" />
      </div>

      {/* Center: Menu items (Always centered on small screens) */}
      <nav className="flex flex-1 justify-center space-x-6 md:space-x-8">
        <Link to="/" className="text-black text-sm sm:text-base font-medium">Home</Link>
        <Link to="/store" className="text-black text-sm sm:text-base font-medium">Store</Link>
        <Link to="/grooming" className="text-black text-sm sm:text-base font-medium">Grooming</Link>
        <Link to="/contact" className="text-black text-sm sm:text-base font-medium">Contact</Link>
      </nav>

      {/* Right side: Icons (Hidden on small screens) */}
      <div className="hidden md:flex items-center space-x-6">
        <Heart size={24} className="text-black" />
        <ShoppingCart size={24} className="text-black" />
        <User size={24} className="text-black" />
      </div>

    </header>
  );
}

export default Navbar;
