import React from "react";

const HeroSection = () => {
  return (
    <div className="bg-[#f4f5ed] p-10 flex flex-col md:flex-row gap-6 items-center">
      {/* Left Side Content */}
      <div className="flex flex-col gap-4 max-w-lg">
        <h1 className="text-5xl font-bold leading-tight">
          Best Help For <br /> Your Lovely Pets
        </h1>
        <p className="text-gray-600">
          Lorem ipsum dolor sit amet consectetur. Id sem amet mattis feugiat dolor turpis ut eu viverra.
        </p>
        <div className="flex gap-4">
          <button className="bg-orange-500 text-white px-6 py-3 rounded-lg">Go To Our Store</button>
          <button className="bg-white border border-gray-300 px-6 py-3 rounded-lg">Book Grooming Section</button>
        </div>
      </div>

      {/* Right Side Image */}
      <div className="relative flex-1">
        <div className="bg-orange-400 h-96 w-full rounded-lg relative">
          <img src="/cat.png" alt="Cat" className="absolute bottom-0 w-full" />
        </div>
      </div>

      {/* Info Cards */}
      <div className="flex flex-col gap-4">
        {[1, 2, 3].map((_, index) => (
          <div key={index} className="bg-white p-4 shadow rounded-lg w-64">
            <h3 className="font-bold">Book Grooming Section</h3>
            <p className="text-gray-500 text-sm">Lorem ipsum dolor sit amet consectetur.</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HeroSection;
