import React from 'react';
import { ArrowUpRight } from 'lucide-react'; // Import arrow icon from Lucide

// CategorySection Component: Displays a grid of pet categories with images
const CategorySection = () => {
  // Static data array containing category information
  const categories = [
    {
      id: 1,
      title: 'Go to All Category',
      bg: 'bg-white',
      isMain: true,
      image: null
    },
    {
      id: 2,
      title: 'Dogs',
      bg: 'bg-[#FFE2CE]',
      image: 'Mask group.png'
    },
    {
      id: 3,
      title: 'Fish',
      bg: 'bg-[#FFE2CE]',
      image: 'envato-labs-image-edit (6) 1.png'
    },
    {
      id: 4,
      title: 'Cats',
      bg: 'bg-[#FFE2CE]',
      image: 'gold-bengal-cat-white-space 2.png'
    },
    {
      id: 5,
      title: 'Birds',
      bg: 'bg-[#FFE2CE]',
      image: 'envato-labs-image-edit (7) 1.png'
    }
  ];

  return (
    <div className="my-24">
      <div className="max-w-7xl mx-auto px-5 md:px-20">
        <h1 className="text-3xl font-bold mb-6">Our Category</h1>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-3">
          <div className="w-full">
            <div className="h-[200px]   bg-white rounded-lg p-6 flex justify-between items-center shadow-sm">
              <span className="text-lg font-medium">Go to All Category</span>
              <ArrowUpRight className="w-6 h-6 text-orange-400" />
            </div>
          </div>
          <div className="h-[200px]  bg-[#FFE2CE]  rounded-lg p-2 flex justify-between items-center">
            <h2 className="text-2xl font-medium">Dogs</h2>
            <img src="Mask group.png" alt="Dog" className="object-contain h-48 w-48" />
          </div>
          <div className="h-[200px]  bg-[#FFE2CE] rounded-lg p-6 flex justify-between items-center">
            <h2 className="text-2xl font-medium">Fish</h2>
            <img src="envato-labs-image-edit (6) 1.png" alt="Fish" className="object-contain h-40 w-52" />
          </div>
          {/* cat box */}
          <div className="md:col-span-2 h-[227px] w-full md:w-[666px] bg-[#FFE2CE] rounded-lg p-6 flex justify-between items-center ">
            <h2 className="text-2xl font-medium">Cats</h2>
            <img src="gold-bengal-cat-white-space 2.png" alt="Cat" className="object-contain h-40 w-96" />
          </div>
          {/* bird box */}
          <div className="mr-[200px] h-[227px] w-full md:w-[666px] bg-[#FFE2CE] rounded-lg p-6 flex justify-between items-center ">
            <h2 className="text-2xl font-medium">Birds</h2>
            <img src="envato-labs-image-edit (7) 1.png" alt="Bird" className="object-contain h-48 w-56" />
          </div>
        </div>
      </div>
    </div>
  );
};

// Export the component for use in other parts of the application
export default CategorySection;
