import React from 'react'
import { Phone, MapPin } from 'lucide-react'

const Header = () => {
  return (
    <header className="bg-[#FF9B57] h-12 flex items-center px-4">
      <div className="flex items-center text-black space-x-4 ml-auto w-full justify-end sm:justify-between">
        {/* Left side: MapPin and Text */}
        <div className="flex items-center space-x-1 sm:space-x-2 mr-4">
          <MapPin size={16} className="sm:size-20 md:size-24" />  {/* Responsive icon size */}
          <span className="text-xs sm:text-sm md:text-base">India</span>  {/* Responsive font size */}
        </div>

        {/* Right side: Phone and Text */}
        <div className="flex items-center space-x-1 sm:space-x-2">
          <Phone size={16} className="sm:size-20 md:size-24" />  {/* Responsive icon size */}
          <span className="text-xs sm:text-sm md:text-base">+123-456-7890</span>  {/* Responsive font size */}
        </div>
      </div>
    </header>
  )
}

export default Header
