import React from 'react';
import { Heart, Star } from 'lucide-react';

const ProductCard = ({ price, discount, description }) => (
  <div className="min-w-[280px] bg-white p-4 rounded-lg shadow-sm snap-start">
    <div className="relative">
      <button className="absolute left-2 top-2 z-10">
        <Heart className="w-6 h-6 text-gray-400" />
      </button>
      <span className="absolute right-2 top-2 bg-orange-500 text-white text-sm px-2 py-1 rounded-md z-10">
        {discount}
      </span>
      <div className="bg-[#faf6f3] rounded-lg p-6 flex justify-center items-center">
        <img 
          src="/api/placeholder/180/220" 
          alt="Product" 
          className="w-32 h-auto"
        />
      </div>
    </div>

    <div className="mt-4">
      <div className="flex mb-2">
        {[1, 2, 3, 4, 5].map((_, index) => (
          <Star 
            key={index}
            className={`w-4 h-4 ${index < 4 ? 'text-yellow-400 fill-yellow-400' : 'text-gray-300'}`}
          />
        ))}
      </div>
      <p className="text-gray-700 mb-2">{description}</p>
      <p className="text-xl font-bold mb-4">â‚¹{price}</p>
      <button className="w-full bg-orange-400 hover:bg-orange-500 text-white py-3 rounded-full transition-colors">
        Add To Cart
      </button>
    </div>
  </div>
);

const ProductCarousel = () => {
  const products = [
    { id: 1, price: 550, discount: '30%', description: 'Lorem ipsum dolor sit amet consectetur.' },
    { id: 2, price: 550, discount: '30%', description: 'Lorem ipsum dolor sit amet consectetur.' },
    { id: 3, price: 550, discount: '30%', description: 'Lorem ipsum dolor sit amet consectetur.' },
    { id: 4, price: 550, discount: '30%', description: 'Lorem ipsum dolor sit amet consectetur.' },
    { id: 5, price: 550, discount: '30%', description: 'Lorem ipsum dolor sit amet consectetur.' },
  ];

  return (
    <div className="bg-[#faf8f6] py-8">
      <div className="max-w-7xl mx-auto px-6">
        <h1 className="text-3xl font-bold mb-6">Our Best Seller</h1>

        {/* Scrollable Container with Hidden Scrollbar */}
        <div className="overflow-x-auto scrollbar-hidden w-full">
          <div className="flex gap-4 snap-x snap-mandatory px-6">
            {products.map((product) => (
              <ProductCard key={product.id} {...product} />
            ))}
            {/* Adding an extra div for right spacing */}
            <div className="min-w-[20px]"></div>
          </div>
        </div>

      </div>
    </div>
  );
};

export default ProductCarousel;
